void twirl(int face) {

    int fb = face * 9;

    char temp = mobileCol[fb + 0];
    mobileCol[fb + 0] = mobileCol[fb + 6];
    mobileCol[fb + 6] = mobileCol[fb + 8];
    mobileCol[fb + 8] = mobileCol[fb + 2];
    mobileCol[fb + 2] = temp;

    temp = mobileCol[fb + 1];
    mobileCol[fb + 1] = mobileCol[fb + 3];
    mobileCol[fb + 3] = mobileCol[fb + 7];
    mobileCol[fb + 7] = mobileCol[fb + 5];
    mobileCol[fb + 5] = temp;

}

void rotateLeft(int layer, int axis) {

    char temp;

    switch (axis) {

    case 0: {


            int faceBase = whichFace[axis] * 9;
            
// 6 7 8
// 3 4 5
// 0 1 2

// 8 5 2
// 7 4 1
// 6 3 0

            char temp;


            if (layer == 0) {

                int fb = 27;            // face 3

                // bottom face

                temp = mobileCol[fb + 0];
                mobileCol[fb + 0] = mobileCol[fb + 6];
                mobileCol[fb + 6] = mobileCol[fb + 8];
                mobileCol[fb + 8] = mobileCol[fb + 2];
                mobileCol[fb + 2] = temp;

                temp = mobileCol[fb + 1];
                mobileCol[fb + 1] = mobileCol[fb + 3];
                mobileCol[fb + 3] = mobileCol[fb + 7];
                mobileCol[fb + 7] = mobileCol[fb + 5];
                mobileCol[fb + 5] = temp;

            }


            if (layer == 2) {

                // handle top face

                temp = mobileCol[faceBase + 0];
                mobileCol[faceBase + 0] = mobileCol[faceBase + 2];
                mobileCol[faceBase + 2] = mobileCol[faceBase + 8];
                mobileCol[faceBase + 8] = mobileCol[faceBase + 6];
                mobileCol[faceBase + 6] = temp;

                temp = mobileCol[faceBase + 1];
                mobileCol[faceBase + 1] = mobileCol[faceBase + 5];
                mobileCol[faceBase + 5] = mobileCol[faceBase + 7];
                mobileCol[faceBase + 7] = mobileCol[faceBase + 3];
                mobileCol[faceBase + 3] = temp;
            }
            

            // handle side ring

            int faceBase1 = whichFace[1] * 9;
            int faceBase2 = whichFace[2] * 9;
            int faceBase4 = whichFace[4] * 9;
            int faceBase5 = whichFace[5] * 9;

            int layerBase = layer * 3;
            for (int i = layerBase; i < layerBase + 3; i++) {
                temp = mobileCol[faceBase1 + i];
                mobileCol[faceBase1 + i] = mobileCol[faceBase2 + i];
                mobileCol[faceBase2 + i] = mobileCol[faceBase4 + i];
                mobileCol[faceBase4 + i] = mobileCol[faceBase5 + i];
                mobileCol[faceBase5 + i] = temp;
            }

        }
        break;

    case 1:

        switch (layer) {
        case 0:
            twirl(4);
            break;

        case 2:
            twirl(2);
            break;
        }

        
        for (int l = layer; l < 9; l += 3) {
            temp = mobileCol[ 0 * 9 + l];
            mobileCol[ 0 * 9 + l ] = mobileCol[ 5 * 9 + l ];
            mobileCol[ 5 * 9 + l ] = mobileCol[ 3 * 9 + l ];
            mobileCol[ 3 * 9 + l ] = mobileCol[ 1 * 9 + l ];
            mobileCol[ 1 * 9 + l ] = temp;
        }

        break;


    case 2:
    {                
        // unteseted

        int faceBase = whichFace[axis] * 9;
        
        if (layer == 0)
            twirl(2);

        if (layer == 2)
            twirl(4);
            
        // handle side ring

        int faceBase1 = whichFace[1] * 9;
        int faceBase2 = whichFace[2] * 9;
        int faceBase4 = whichFace[4] * 9;
        int faceBase5 = whichFace[5] * 9;

        int layerBase = layer * 3;
        for (int i = layerBase; i < layerBase + 3; i++) {
            char temp = mobileCol[faceBase1 + i];
            mobileCol[faceBase1 + i] = mobileCol[faceBase2 + i];
            mobileCol[faceBase2 + i] = mobileCol[faceBase4 + i];
            mobileCol[faceBase4 + i] = mobileCol[faceBase5 + i];
            mobileCol[faceBase5 + i] = temp;
        }

    }
    break;


    default:
    break;
    }


}



void resetMobileEndpoints() {

    for (int side = 0; side < 6; side++) {
        for (int square = 0; square < 9; square++) {
            int destFacet = side * 9 + square;
            int facet = side * 9 + square; // whichFace[side] * 9 + square;
            mobileFacetEndX[destFacet] = facetEndX[facet];
            mobileFacetEndY[destFacet] = facetEndY[facet];
        }
    }
}



