void setPalette(int start, int size, int step) {

    setColours();

    int bgCharLine = (scrollY >> 16) * 3;
    int absLine = 0;
    int pfCharLine = 0;
    
    if (start) {
        absLine = bgCharLine;
        bgCharLine--;

        while (bgCharLine >= size) {
            bgCharLine -= size;
            pfCharLine++;
        }
    }



    int i = start;
    while (i < _ARENA_SCANLINES) {


        RAM[_BUF_COLUPF + i] = ColourConvert(bgPalette[pfCharLine]);
        
            
        bgCharLine += 3;            
        if (bgCharLine >= size) {
            bgCharLine = 0;
            pfCharLine++;
        }


        absLine += step;
        i+=3;
    }

}





