int rr = 0;
int rr2 = 0;


void drawSoftwareSprites() {


    if (drawMode == 0) {
    // if (facetIndex < 54) {
        drawOverviewSoftwareSprites();
        return;
    }



    rr = rotateTop[fLayer] & 3;


    //   if (fLayer == 1)
    //        rr = 1;

    struct facet *f = &shapeDef[axis][rr][fLayer][fno++];

    // no more facets --> finished drawing
    if (f->shape < 0) {

        fno = 0;


        if (++fLayer > 2) {

            fLayer = 0;
            finished = true;
            rr2++;

            if (highlightLayer) {
                highlightLayer--;
                showHighlight=  true;
            }
            else
                showHighlight = false;


            if (changedLayer) {
                changedLayer--;
            }

            for (int l = 0; l < 3; l++) {

                if (rotateSpeed[l]) {
                    rotateSpeed[l]--;
                }

    
                if (direct[l] && !rotateSpeed[l]) {


                    rotateSpeed[l] = 1;
                    rotateTop[l]+= direct[l];
                    rotateTop[l] &= 3;


                    if (direct[l] < 0 && (rotateTop[l] & 3) == 3) {
                        rotateLeft(l, axis);
                        rotateLeft(l, axis);
                        rotateLeft(l, axis);
                        //direct[l] = 0;
                    }


                    if (direct[l] > 0 && !(rotateTop[l] & 3)) {
                        rotateLeft(l, axis);
                        direct[l] = 0;
                    }



                    if (direct[l] < 0 && !(rotateTop[l] & 3))
                        direct[l] = 0;


                }
            }
        }


        return;

    }



    int theColour = mobileCol[whichFace[f->face] * 9 + f->square];

    if ((showHighlight && fLayer == controlledLayer ) && !(highlightLayer & 1))
        theColour = 0;

    char shape = f->shape;
    // if (shape & 0x80) {
    //     if (controlledLayer > 0 && rotateTop[controlledLayer-1]) {
    //         shape = shape & 0x7f;
    //     }
    // }

    // if (!(shape & 0x80))


    // if (fLayer == 2)
    //     return;

        drawBitmap(whichShapeSet[shape][0],theColour,
            ((f->x << 14) & 0xFFFFC000) + 0x00014000,
            f->y * 3);
  
}



void GameVerticalBlank() {
 

    if (finished) {

        if (swap) {
            drawMode ^= 1;
            fLayer = 0;
            fno = 0;
            rspeed = 0x300;
            rxspeed = 0;
            facetIndex = 0;
            swap = false;
        }


        pfBuffer ^= 1;
        Scroll();
        drawScreen();
        finished = false;
    }

    HandleJoystick();

    frameCounter++;

    InitGameDatastreams();

}


void SetupBoard() {

    if (frameCounter > gameSpeed) {

        frameCounter = 0;
        gameSchedule = SCHEDULE_PROCESSBOARD;

    }
}



void InitGameBuffers() {
    
    // set the Jump Datastream so each entry runs the NORMAL KERNEL by default
    // init Jump Datastream
    for (int i=0; i < _ARENA_SCANLINES; i++)
        RAM_SINT[(_BUF_JUMP1 / 2) + i] = _NORMAL_KERNEL;
    RAM_SINT[ _BUF_JUMP1_EXIT / 2 ] = _EXIT_KERNEL;
}



void GameScheduleProcessBoardRow() {

    if (!finished)
        drawSoftwareSprites();

}




