void formLandscape(){


    int horizon = (((getRandom32() & 0xFF) * 4) >> 8) + 8;
    for (int i = 1; i < 39; i++) {

        int offset = (((getRandom32() & 0xFF) * 3) >> 8) - 1;
        horizon += offset;

        if (horizon < 1) 
            horizon = 1;

        if (horizon > 22)
            horizon = 22;


        for (int j = 1; j < horizon; j++)
            setChar(i,j, CH_BLANK); //_PARALLAX);


    }

}


